sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */


	data.ownerID = gs.getUserID();

	data.newRental= {
		item: "",
		time_from: "",
		time_to: "",
		total_price: "",
		renter: data.ownerID
	}
	data.items = new SPortalUtils().getItems();


	if(input && input.action == "createRental"){
		new SPortalUtils().createRental(input.newRental);
	}


})();
